mapper.update:
UPDATE ${table} board
SET board.view = board.view + 1
WHERE board.id = #{id}

mapper.select:
SELECT * FROM(
    (SELECT *, 'Free' AS tblName FROM post_free ORDER BY id DESC LIMIT 5)
    UNION ALL
    (SELECT *, 'Notice' AS tblName FROM post_notice ORDER BY id DESC LIMIT 5)
) qry ORDER BY date_post DESC LIMIT 5;
